mapper.update:
UPDATE TB_HOMEPAGE_INFO SET
                            HOMEPAGE_ADMIN = #{homepage_admin},
                            HOMEPAGE_ADMIN_PNUM = #{homepage_admin_pnum},
                            EMAIL=#{email}
WHERE IDX_HOMEPAGE_INFO = #{idx_homepage_info}

UPDATE TB_HOMEPAGE_INFO SET
    IS_CURRENT = 2
WHERE IDX_HOMEPAGE_INFO = #{idx_homepage_info}

UPDATE TB_HOMEPAGE_INFO SET
    IS_CURRENT = 1
WHERE IDX_HOMEPAGE_INFO = #{idx_homepage_info}

UPDATE TB_HOMEPAGE_INFO SET
    IS_CURRENT = 0
WHERE IDX_HOMEPAGE_INFO = #{idx_homepage_info}
