mapper.update:
UPDATE TB_USER_DEMO_BS SET
    USER_DEMOBS_STATUS = #{user_demobs_status}
WHERE IDX_USER_DEMO_BS = #{idx_user_demo_bs}

UPDATE TB_USER_DEMO_BS SET
LAB_EST_DATE = #{lab_est_date}, RND_RATE = #{rnd_rate}, CONSOLIDATED_COMPANY = #{consolidated_company}, HISTORY1 = #{history1}, HISTORY1_DATE = #{history1_date}, HISTORY2 = #{history2}, HISTORY2_DATE = #{history2_date}, HISTORY3 = #{history3}, HISTORY3_DATE = #{history3_date}, PRIZE1 = #{prize1}, PRIZE1_DATE = #{prize1_date}, PRIZE1_ORG = #{prize1_org}, PRIZE1_ETC = #{prize1_etc}, PRIZE2 = #{prize2}, PRIZE2_DATE = #{prize2_date}, PRIZE2_ORG = #{prize2_org}, PRIZE2_ETC = #{prize2_etc}, PRIZE3 = #{prize3}, PRIZE3_DATE = #{prize3_date}, PRIZE3_ORG = #{prize3_org}, PRIZE3_ETC = #{prize3_etc}, DEMO_START_DATE = #{demo_start_date}, DEMO_END_DATE = #{demo_end_date}, FARMING_START_DATE = #{farming_start_date}, FARMING_END_DATE = #{farming_end_date}, FARMING_REPEAT = #{farming_repeat},
