mapper.select:
SELECT IDX_USER_DEMO_BS ,ANNOUNCE_NUM ,SUBJECT ,BS_STATUS ,IS_FILE ,IS_NEW ,AUTHOR ,POSTING_START_DATE ,VIEW_COUNT FROM ( SELECT ROW_NUMBER() OVER(ORDER by #{order_field} DESC) AS ROW_NUM ,IDX_BS_ANNOUNCEMENT ,ANNOUNCE_NUM ,SUBJECT ,BS_STATUS ,IS_FILE ,IS_NEW ,AUTHOR ,POSTING_START_DATE ,VIEW_COUNT FROM ( SELECT * FROM TB_BS_ANNOUNCEMENT
WHERE DEMO_BS_STATUS = #{filter1}
OR DEMO_BS_STATUS = #{filter2}
)
WHERE ROWNUM <= #{cur_page}*#{list_amount} ) WHERE ROW_NUM > (#{cur_page} -1)*#{list_amount}

SELECT IDX_USER_DEMO_BS ,IDX_DEMO_BUSINESS ,USER_DEMOBS_STATUS ,IDX_USER ,USER_DEMO_BS_TYPE ,IDX_CORP_INFO ,CORP_NAME ,CORP_NUM ,CORP_PHONE ,EMAIL ,REQ_FACILITY ,CEO_NAME ,CEO_MNUMBER ,CEO_EMAIL ,MAN_NAME ,MAN_MNUMBER ,MAN_EMAIL ,USER_DEMO_TYPE ,USER_DEMO_FACILITY ,USER_DEMO_WAY ,USER_DEMO_GOAL ,CULTURE_SOIL ,USER_DEMO_IS_CROPS ,DEMO_START_DATE ,DEMO_END_DATE ,RESIDENT_TYPE ,REG_DATE ,LAST_UPD_DATE FROM ( SELECT ROW_NUMBER() OVER(ORDER by #{order_field} DESC) AS ROW_NUM ,A.*
FROM ( SELECT * FROM TB_USER_DEMO_BS WHERE IDX_DEMO_BUSINESS =#{idx}
AND USER_DEMOBS_STATUS >= #{filter1}
AND USER_DEMOBS_STATUS < #{filter2}
) A
WHERE ROWNUM <= #{cur_page}*#{list_amount} ) WHERE ROW_NUM > (#{cur_page} -1)*#{list_amount}
