mapper.select:
SELECT
        *
FROM (
    SELECT ROW_NUMBER() OVER(ORDER by #{order_field} DESC) AS row_num
        t.*
    FROM TB_USER t
    WHERE ROWNUM <= #{page_num}*#{amount}
    )
WHERE row_num > (#{page_num} -1)*#{amount}

mapper.update:
UPDATE TB_USER
SET
    USER_PW =  #{user_pw}
WHERE USER_ID=#{user_id}

mapper.select:
SELECT * FROM (
SELECT A.*, FLOOR((ROWNUM - 1)/#{count} + 1) PAGE, (
SELECT MAX(FLOOR((ROWNUM - 1)/#{count} + 1)) FROM TB_ADMIN
WHERE IDX_ADMIN >= 1

    AND CORPORATE = #{corporate}

) MAXVALUE FROM (
SELECT TB_ADMIN.* FROM TB_ADMIN
WHERE IDX_ADMIN >= 1

    AND CORPORATE = #{corporate}

ORDER BY CONVERT(ADMIN_NAME,'UTFE') ASC
)
A)
WHERE PAGE = #{pageNum}
