mapper.select:
SELECT IDX_VISIT_REQ, VISIT_REQ_NUM, GROUP_NAME, VISITOR, RESULVATION_DATE, VISITOR_COUNT, VISITOR_MPHONE_NUM, VISITOR_EMAIL, VISITOR_ADRESS, VISIT_GOAL, QUESTION, VISIT_REQ_STATUS, IS_PRIVACY_ACCEPT, IS_GROUP, IS_CANCEL, IS_MANAGER, IDX_MANAGER, IS_DURATION, DURATION_AMOUNT, MEMO, IDX_VISIT_DATA, IDX_USER, IDX_ADMIN, REG_DATE FROM ( SELECT ROW_NUMBER() OVER(ORDER by #{order_field} DESC) AS row_num ,IDX_VISIT_REQ, VISIT_REQ_NUM, GROUP_NAME, VISITOR, RESULVATION_DATE, VISITOR_COUNT, VISITOR_MPHONE_NUM, VISITOR_EMAIL, VISITOR_ADRESS, VISIT_GOAL, QUESTION, VISIT_REQ_STATUS, IS_PRIVACY_ACCEPT, IS_GROUP, IS_CANCEL, IS_MANAGER, IDX_MANAGER, IS_DURATION, DURATION_AMOUNT, MEMO, IDX_VISIT_DATA, IDX_USER, IDX_ADMIN, REG_DATE FROM ( SELECT * FROM TB_VISIT_REQ order by IDX_VISIT_REQ desc )
WHERE ROWNUM <= #{page_num}*#{amount}
AND visit_req_status = #{filter1}
) WHERE row_num > (#{page_num} -1)*#{amount}

SELECT COUNT (*) FROM TB_VISIT_REQ

    WHERE visit_req_status = #{filter1}

ORDER BY VISIT_REQ_NUM ASC

mapper.update:
UPDATE TB_VISIT_DATA SET
                         VISIT_DATA_TYPE = #{visit_data_type}
WHERE IDX_VISIT_DATA = #{idx_visit_data}
