function preprocessKeyword(a) {
    var d = a;
    var b;
    var m;
    var l = /([0-9]|번지)$/;
    var k = /번지$/;
    var e = /^([0-9]|-)$/;
    if (l.test(a)) {
        d = a.split(k).join("");
        m = d.length;
        for (var c = m - 1; c >= 0; c--) {
            b = d.charAt(c);
            if (!e.test(b)) {
                if (b != " ") {
                    d = insertString(d, c + 1, " ")
                }
                break
            }
        }
    }
    var j = /대로[ ]+/;
    var h = /[0-9]+번길/;
    if (j.test(d)) {
        if (h.test(d)) {
            d = d.replace(j, "대로")
        }
    }
    var g = /로[ ]+/;
    var f = /[0-9]+길/;
    if (g.test(d)) {
        if (f.test(d)) {
            d = d.replace(g, "로")
        }
    }
    return d
}

function insertString(c, b, a) {
    if (b > 0) {
        return c.substring(0, b) + a + c.substring(b, c.length)
    } else {
        return a + c
    }
}

function checkRestrictedWord(c) {
    var a = c;
    if (a.length <= 0) {
        return a
    }
    var g = /[%=><]/;
    if (g.test(a) == true) {
        a = a.split(g).join("")
    }
    var f = new Array("OR", "SELECT", "INSERT", "DELETE", "UPDATE", "CREATE", "DROP", "EXEC", "UNION", "FETCH", "DECLARE", "TRUNCATE");
    var d;
    var e;
    for (var b = 0; b < f.length; b++) {
        d = new RegExp("\\s" + f[b] + "\\s", "gi");
        if (d.test(a)) {
            a = a.replace(d, "")
        }
        e = new RegExp("\\+" + f[b] + "\\+", "gi");
        if (e.test(a)) {
            a = a.replace(e, "")
        }
    }
    return a
}

