var entityMap = {
    "&": "\\&",
    "<": "\\<",
    ">": "\\>",
    '"': '\\"',
    "'": "\\'",
    "/": "\\/",
    "`": "\\`",
    "=": "\\="
};

function escapeHtml(a) {
    return String(a).replace(/[&<>"'`=\/]/g, function(b) {
        return entityMap[b]
    })
}

